package myoperatortest;

public class AssignmentOperators {

    public static void main(String[] args) {
        // TODO Auto-generated method stub
        int x = 12;
        x+=3;
        System.out.println(x);
        x-=6;
        System.out.println(x);
        x*=100;
        System.out.println(x);
        x/=60;
        System.out.println(x);
        x%=5;
        System.out.println(x);

    }

}
